vector<vector<int>> generateMatrix(int n) {
	vector<vector<int>> res(n, vector<int>(n));//!!!!!!!!!!!!
	int i, j, k, l;
	int c = 1;
	i = j = 0;
	k = l = n - 1;
	while (c <= n*n) 
	{
		for (int m = j; m <= l; m++)
		{
			res[i][m] = c;
			c++;
		}
		i++;
		for (int m = i; m <= k; m++)
		{
			res[m][l] = c;
			c++;
		}
		l--;
		for (int m = l; m >= j; m--)
		{
			res[k][m] = c;
			c++;
		}
		k--;
		for (int m = k; m >= i; m--)
		{
			res[m][j] = c;
			c++;
		}
		j++;
	}
	return res;
}
