```c
 vector<int> spiralOrder(vector<vector<int>>& matrix) {
    vector<int>res;
    if(matrix.empty())
        return res;
	int m = matrix.size();
	int n = matrix[0].size();
	int a[50][50];
	memset(a, 0, sizeof(a));
	int k = 0, i, j;
	int q, w, e, r;
	q = r = 0;
	w = n-1;
	e = m-1;
	while (k < m*n)
	{
		for (i = 0; i < n; i++)
		{
			if (a[q][i] == 0)
			{
				res.push_back(matrix[q][i]);
				a[q][i] = 1;
				k++;
			}
