		q++;
		for (i = 0; i < m; i++)
		{
			if (a[i][w] == 0)
			{
				res.push_back(matrix[i][w]);
				a[i][w] = 1;
				k++;
			}
		}
		w--;
		for (i = n - 1; i >= 0; i--)
		{
			if (a[e][i] == 0)
			{
				res.push_back(matrix[e][i]);
				a[e][i] = 1;
				k++;
			}
		}
		e--;
		for (i = m - 1; i >= 0; i--)
		{
			if (a[i][r] == 0)
			{
				res.push_back(matrix[i][r]);
				a[i][r] = 1;
				k++;
			}
		}
		r++;
	}
	return res;
}
