	for (i = 0; i < board.size(); i++)
	{
		for (j = 0; j < board[i].size(); j++)
		{
			if (board[i][j] == word[0])
			{
				find(i, j, 0, word, board, res,visited);
				if (res)
					break;
			}
			if (res)
				break;
		}
	}
	return res;
}
